import IaXmlWriter from "../../../../Xml/IaXmlWriter";
import ISelect from "./ISelect";
export default abstract class AbstractSelectFunction implements ISelect {
    private readonly fieldName;
    static readonly AVERAGE = "avg";
    static readonly MINIMUM = "min";
    static readonly MAXIMUM = "max";
    static readonly COUNT = "count";
    static readonly SUM = "sum";
    constructor(fieldName: string);
    /**
     * Returns aggregate function name
     *
     * @return {string}
     */
    abstract getFunctionName(): string;
    writeXml(xml: IaXmlWriter): void;
}
